import { build } from "vite";
const handler = async ({ scope }) => {
  if (scope) {
    process.env.YEXT_PAGES_SCOPE = scope;
  }
  await build();
};
const buildCommand = (program) => {
  program.command("build").description("Build site using Vite").option(
    "--scope <string>",
    "The subfolder to scope the served templates from"
  ).action(handler);
};
export {
  buildCommand
};
