const getYextUrlForPartition = (universe, partition) => {
  let urlPrefix = "";
  switch (partition) {
    case "US":
      switch (universe) {
        case "production":
        case "prod":
          break;
        case "sandbox":
        case "sbx":
          urlPrefix = "sandbox.";
          break;
        case "qa":
          urlPrefix = "qa.";
          break;
        case "dev":
          urlPrefix = "dev.";
          break;
        default:
          return "";
      }
      break;
    case "EU":
      switch (universe) {
        case "production":
        case "prod":
          urlPrefix = "app.eu.";
          break;
        case "qa":
          urlPrefix = "app-qa.eu.";
          break;
        default:
          return "";
      }
      break;
    default:
      return "";
  }
  return `${urlPrefix}yext.com`;
};
export {
  getYextUrlForPartition
};
